
var TAMANHO_RESPONSIVO=768;$(document).ready(function(){$(window).resize(tabsResponsivo);$(window).trigger("resize")});function tabsResponsivo(){var b=document.documentElement.clientWidth;if(b<=TAMANHO_RESPONSIVO){if($(".select-menu").length===0){var a=$("<select></select>");a.addClass("select-menu seletorMobile");$(".nav-tabs:not(.campoDesktop) li a").each(function(){var c=$("<option></option>");c.val($(this).attr("href"));c.text($(this).text());if($(this).parent().hasClass("active")){c.attr("selected",true)}a.append(c)});a.change(function(){if(this.value.match("^http://")){window.location.href=this.value}else{$(".nav-tabs a[href="+this.value+"]").tab("show")}});$(".nav-tabs:not(.campoDesktop)").before(a).hide()}}if(b>TAMANHO_RESPONSIVO){$(".select-menu").remove();$(".nav-tabs").show()}};